import React, { useEffect, useState } from "react";

function CoinCourse() {
  const [answer, setAnwser] = React.useState(['empty']);

    useEffect(() => {
        fetch("http://api.coinlayer.com/api/live?access_key=b2d197d9a597eb5755a5aa866c7b8c28")
        .then(response => response.json())
        .then(answer => {
            setAnwser(answer);
            let arr = answer.rates;
            const resArray = [];
            resArray.push(arr);
            courseData();
            console.log(answer);
            console.log(resArray);
            console.log(resArray[0].BTC);})
            
                let courseData = function() {return (           
                    <div>
                    <ul>{resArray[0].BTC}
                        {resArray && resArray.map((item, index) => <li key={index}>BTC - {item.BTC}$</li>)}
                        {resArray && resArray.map((item, index) => <li key={index}>ETH - {item.ETH}$</li>)}
                        {resArray && resArray.map((item, index) => <li key={index}>BNB - {item.BNB}$</li>)}
                        {resArray && resArray.map((item, index) => <li key={index}>DOGE - {item.DOGE}$</li>)}
                    </ul>  
                    </div>  
                );}
                
            
    }, []); 
}
export default CoinCourse;
