import React, { useEffect, useState } from "react";

function CoinCourse() {
  const [answer, setAnwser] = useState(['empty']);

    useEffect(() => {
        async function loadData() {
        fetch("http://api.coinlayer.com/api/live?access_key=a8bd7dbefe44ea662b7d1816d78909ff")
        .then(response => response.json())
        .then(answer => {
            setAnwser(answer);
            let arr = answer.rates;
            const resArray = [];
            resArray.push(arr);
            console.log(answer);
            console.log(resArray); 
            })
        }
        loadData();
    }, []);

        // let arr = answer.rates;
        // const resArray = [];
        // resArray.push(arr); 

    
        return (           
        <div>
        <ul>
             {resArray && resArray.map((item, index) => <li key={index}>BTC - {item.BTC}$</li>)}
             {resArray && resArray.map((item, index) => <li key={index}>ETH - {item.ETH}$</li>)}
             {resArray && resArray.map((item, index) => <li key={index}>BNB - {item.BNB}$</li>)}
             {resArray && resArray.map((item, index) => <li key={index}>DOGE - {item.DOGE}$</li>)}
        </ul>  
        </div>  
        );   

}
export default CoinCourse;
