function CoinCourse() {
  const [answer, setAnwser] = React.useState([]);

    useEffect(() => {
        fetch("http://api.coinlayer.com/api/live?access_key=a8bd7dbefe44ea662b7d1816d78909ff")
        .then(response => response.json())
        .then(answer => setAnwser(answer))
    }, []);

        let arr = answer.rates; //выбор нужного элемента
        const resArray = []; //преобразование json в массив
        resArray.push(arr); 
        // console.log(resArray);

        return (           
        <div>
        <ul>{<div>{resArray[0]}</div>}
             {/* {resArray && resArray.map((item, index) => <li key={index}>BTC - {item.BTC}$</li>)}
