import React, { useEffect, useState } from "react";
import Loader from "../Loader";

function CoinCourse() {
  const [answer, setAnwser] = useState(['empty']);

    useEffect(() => {
        fetch("http://api.coinlayer.com/api/live?access_key=df026db8052485090e7235fd206a9d68")
        .then(response => response.json())
        .then(answer => {
            setAnwser(answer);})
    }, []); 

            const resArray = ['loading'];
            resArray.push(answer.rates);
            console.log(resArray);

            if (resArray[1] === undefined) {return (<Loader></Loader>)} else
                {return (           
                    <div>
                    <ul>
                        <li><img src='https://cdn-icons-png.flaticon.com/512/991/991959.png' width="20px" height="20px"></img>
                         BTC - {resArray[1].BTC} $</li>
                        <li><img src='https://cdn-icons-png.flaticon.com/512/7829/7829596.png' width="20px" height="20px"></img>
                         ETH - {resArray[1].ETH} $</li>
                        <li><img src='https://cdn-icons-png.flaticon.com/512/7403/7403664.png' width="20px" height="20px"></img>
                        BNB - {resArray[1].BNB} $</li>
                        <li><img src='https://cdn-icons-png.flaticon.com/512/1289/1289711.png' width="20px" height="20px"></img>
                        LTC - {resArray[1].LTC} $</li>
                        <li><img src='https://cdn-icons-png.flaticon.com/512/7829/7829576.png' width="20px" height="20px"></img>
                        DOGE - {resArray[1].DOGE} $</li>
                        <li><img src='https://cdn-icons-png.flaticon.com/512/7829/7829536.png' width="20px" height="20px"></img>
                        ADA - {resArray[1].ADA} $</li>
                    </ul>  
                    </div>  
                );}   
}
export default CoinCourse;
