import React, { useEffect, useState } from "react";
import Loader from "../Loader";

function CoinCourse() {
  const [answer, setAnwser] = useState(['empty']);

    useEffect(() => {
        fetch("http://api.coinlayer.com/api/live?access_key=034298dfb27562e1c7f6c9c012e1cebb")
        .then(response => response.json())
        .then(answer => {
            setAnwser(answer);})
    }, []); 

            const resArray = ['loading'];
            resArray.push(answer.rates);
            console.log(resArray);

            if (resArray[1] === undefined) {return (<Loader></Loader>)} else
                {return (           
                    <div>
                    <ul>
                        <li><img src='https://e7.pngegg.com/pngimages/954/362/png-clipart-bitcoin-scalable-graphics-cryptocurrency-computer-icons-bitcoin-text-trademark.png' width="25px" height="25px"></img>BTC - {resArray[1].BTC} $</li>
                        <li>ETH - {resArray[1].ETH} $</li>
                        <li>BNB - {resArray[1].BNB} $</li>
                        <li>LTC - {resArray[1].LTC} $</li>
                        <li>DOGE - {resArray[1].DOGE} $</li>
                        <li>ADA - {resArray[1].ADA} $</li>
                    </ul>  
                    </div>  
                );}   
}
export default CoinCourse;
